#!usr/bin/env python3

dna = ''
print ('The DNA sequence is',dna)

A = dna.count ('A')
T = dna.count ('T')
print ('A =', A)
print ('T =', T)

total_count = dna.count ('A') + dna.count ('T') + dna.count ('C') + dna.count ('G')

percent_GC_content = '{:.1%}'.format ((dna.count ('G') + dna.count ('C')) / total_count)

print ('The GC percentage is',percent_GC_content)




